import styled from "styled-components";

export const TilesContainer = styled.div`
  display: grid;
  grid-template-columns: 4;
  margin-top: 16px;
  background: red;
  width: 100%;

  .single-tile {
    display: flex;
    flex-direction: column;
    margin-bottom: 8px;
    border: 1px solid black;
    padding: 4px;
    min-width: 200px;

    span {
      color: black;
      font-size: 18px;
      text-align: left;
    }

    button {
      background: transparent;
      border: none;
      margin: 0;
      padding: 0;
      text-align: left;
    }
    &__name {
      color: blue;
      text-decoration: underline;
    }
  }
`;
