import React from "react";
import {
  CartesianGrid,
  Legend,
  Line,
  LineChart,
  ResponsiveContainer,
  Tooltip,
  XAxis,
  YAxis,
} from "recharts";
import * as Styled from "./styles";

export const StockCandles = ({
  setIndex,
  stockCandles,
  INTERVAL_OPTIONS,
  setInterval,
}) => {
  const transformData = (data) => {
    return data.c.map((item, index) => ({
      close: Number(item).toFixed(2),
      open: Number(data.o[index]).toFixed(2),
      timestamp: new Date(data.t[index] * 1000).toLocaleDateString(),
    }));
  };

  const handleChangeInterval = (event) => setInterval(event.target.value);

  return (
    <Styled.StockCandlesContainer>
      <Styled.TopSection>
        <button onClick={() => setIndex(0)}>Back to list</button>
        <select onChange={handleChangeInterval}>
          {INTERVAL_OPTIONS.map((s) => (
            <option key={s} value={s}>
              {s === 1 ? `Past ${s} day` : `Past ${s} days `}
            </option>
          ))}
        </select>
      </Styled.TopSection>
      <div>
        {stockCandles.s === "ok" ? (
          <ResponsiveContainer width="100%" height={400}>
            <LineChart
              width={900}
              height={500}
              data={transformData(stockCandles)}
              margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
              className="line-chart"
            >
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="timestamp" />
              <YAxis
                type="number"
                allowDecimals={true}
                allowDataOverflow={true}
              />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="open" stroke="blue" dot={false} />
              <Line type="monotone" dataKey="close" stroke="gray" dot={false} />
            </LineChart>
          </ResponsiveContainer>
        ) : (
          "No data"
        )}
      </div>
    </Styled.StockCandlesContainer>
  );
};
