import * as Styled from "./styles";
import React, { useState } from "react";
import { validateDate } from "../../validator/company-profiles";
import axios from "axios";

export const CompanyProfiles = ({
  companyProfileValues,
  count,
  getCompanyProfiles,
  symbols,
  searchValue,
  setErrors,
}) => {
  const [stockCandles, setStockCandles] = useState({});

  const handleClick = () => {
    let validationResults = validateDate(searchValue, setErrors);

    if (Object.keys(validationResults).length === 0) {
      console.log("NICEEE!");
    }
  };

  const getStockCandles = () => {
    const FINNGUB_STOCK_CANDLES_API = `${process.env.REACT_APP_FINNHUB_STOCK_CANDLES_API}?symbol=${searchValue.name}&resolution=1&from=${searchValue.dateFrom}&to=${searchValue.dateTo}&token=${process.env.REACT_APP_FINNHUB_TOKEN}`;

    try {
      axios.get(FINNGUB_STOCK_CANDLES_API).then((response) => {
        console.log("STOCK CANDLES", response.data);
        setStockCandles(response.data);
      });
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <Styled.Section>
      <Styled.TilesContainer>
        {companyProfileValues?.map((companyProfile, index) => (
          <div key={index} className="single-tile">
            <button onClick={handleClick}>
              <p>name : {companyProfile.name}</p>
            </button>
            <p>country : {companyProfile.country}</p>
            <p>currency : {companyProfile.currency}</p>
            <p>web url : {companyProfile.weburl}</p>
          </div>
        ))}
      </Styled.TilesContainer>
      {companyProfileValues.length < count && (
        <button
          onClick={() =>
            getCompanyProfiles(
              symbols,
              companyProfileValues.length + 1,
              companyProfileValues.length + 5
            )
          }
        >
          Get more
        </button>
      )}
    </Styled.Section>
  );
};
