export const validateCompanyName = (values, setError) => {
  const errors = {};
  const regex = /^[a-zA-Z ]{3,35}$/;

  if (!values.name) {
    errors.name = "Company name is required";
  } else if (!regex.test(values.name)) {
    errors.name = "Company name must be at least 2 symbols";
  }
