import * as Styled from "./styles";
import React, { useState, useEffect } from "react";
import { validateDate } from "../../validator/company-profiles";
import axios from "axios";
import { StockCandles } from "../stock-candles/stock-candles";

export const CompanyProfiles = ({
  companyProfileValues,
  count,
  getCompanyProfiles,
  symbols,
  searchValue,
  setErrors,
}) => {
  const [stockCandles, setStockCandles] = useState({});
  const [index, setIndex] = useState(0);

  const INTERVAL_OPTIONS = [1, 5, 15, 30, 60];
  const [interval, setInterval] = useState(INTERVAL_OPTIONS[0]);

  const handleClick = () => {
    let validationResults = validateDate(searchValue, setErrors);

    if (Object.keys(validationResults).length === 0) {
      handleStockCandles();
    }
  };

  const getStockCandles = async () => {
    return new Promise((resolve, reject) => {
      const FINNGUB_STOCK_CANDLES_API = `${process.env.REACT_APP_FINNHUB_STOCK_CANDLES_API}?symbol=${searchValue.name}&resolution=${interval}&from=${searchValue.dateFrom}&to=${searchValue.dateTo}&token=${process.env.REACT_APP_FINNHUB_TOKEN}`;

      axios
        .get(FINNGUB_STOCK_CANDLES_API)
        .then((response) => {
          resolve(response.data);
        })
        .catch((err) => reject(err));
    });
  };

  const handleStockCandles = async () => {
    try {
      const responseCandles = await getStockCandles();
      setStockCandles(responseCandles);
      setIndex(1);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    handleStockCandles();
  }, [interval]);

  return (
    <Styled.Section>
      {index === 1 ? (
        <StockCandles
          setIndex={setIndex}
          stockCandles={stockCandles}
          setInterval={setInterval}
          INTERVAL_OPTIONS={INTERVAL_OPTIONS}
        />
      ) : (
        <>
          <Styled.TilesContainer>
            {companyProfileValues?.map((companyProfile, index) => (
              <div key={index} className="single-tile">
                <button onClick={handleClick}>
                  <p>name : {companyProfile.name}</p>
                </button>
                <p>country : {companyProfile.country}</p>
                <p>currency : {companyProfile.currency}</p>
                <p>web url : {companyProfile.weburl}</p>
              </div>
            ))}
          </Styled.TilesContainer>
          {companyProfileValues.length < count && (
            <button
              onClick={() =>
                getCompanyProfiles(
                  symbols,
                  companyProfileValues.length + 1,
                  companyProfileValues.length + 5
                )
              }
            >
              Get more
            </button>
          )}
        </>
      )}
    </Styled.Section>
  );
};
