import * as Styled from "./styles";
import React, { useState } from "react";
import { validateDate } from "../../validator/company-profiles";
import axios from "axios";
import { StockCandles } from "../stock-candles/stock-candles";

export const CompanyProfiles = ({
  companyProfileValues,
  count,
  getCompanyProfiles,
  symbols,
  searchValue,
  setErrors,
}) => {
  const [stockCandles, setStockCandles] = useState({});
  const [index, setIndex] = useState(0);

  const handleClick = async () => {
    let validationResults = validateDate(searchValue, setErrors);

    if (Object.keys(validationResults).length === 0) {
      try {
        const responseCandles = await getStockCandles();
        console.log("responseCandles", responseCandles);
        setIndex(1);
      } catch (error) {
        console.log(error);
      }
    }
  };

  const getStockCandles = async () => {
    return new Promise((resolve, reject) => {
      const FINNGUB_STOCK_CANDLES_API = `${process.env.REACT_APP_FINNHUB_STOCK_CANDLES_API}?symbol=${searchValue.name}&resolution=D&from=${searchValue.dateFrom}&to=${searchValue.dateTo}&token=${process.env.REACT_APP_FINNHUB_TOKEN}`;

      axios
        .get(FINNGUB_STOCK_CANDLES_API)
        .then((response) => {
          resolve(response.data);
        })
        .catch((err) => reject(err));
    });
  };
  return (
    <Styled.Section>
      {index === 1 ? (
        <StockCandles setIndex={setIndex} stockCandles={stockCandles} />
      ) : (
        <>
          <Styled.TilesContainer>
            {companyProfileValues?.map((companyProfile, index) => (
              <div key={index} className="single-tile">
                <button onClick={handleClick}>
                  <p>name : {companyProfile.name}</p>
                </button>
                <p>country : {companyProfile.country}</p>
                <p>currency : {companyProfile.currency}</p>
                <p>web url : {companyProfile.weburl}</p>
              </div>
            ))}
          </Styled.TilesContainer>
          {companyProfileValues.length < count && (
            <button
              onClick={() =>
                getCompanyProfiles(
                  symbols,
                  companyProfileValues.length + 1,
                  companyProfileValues.length + 5
                )
              }
            >
              Get more
            </button>
          )}
        </>
      )}
    </Styled.Section>
  );
};
