import React from "react";
import { StockCandlesDiagram } from "./stock-candles-diagram/stock-candles-diagram";

import * as Styled from "./styles";

export const StockCandles = ({
  setIndex,
  stockCandles,
  INTERVAL_OPTIONS,
  setInterval,
}) => {
  const handleChangeInterval = (event) => setInterval(event.target.value);
  console.log("stockCandles.s ===", stockCandles.s === "ok");

  return (
    <Styled.StockCandlesContainer>
      <Styled.TopSection>
        <button onClick={() => setIndex(0)}>Back to list</button>
        <select onChange={handleChangeInterval}>
          {INTERVAL_OPTIONS.map((s) => (
            <option key={s} value={s}>
              {s === 1 ? `Past ${s} day` : `Past ${s} days `}
            </option>
          ))}
        </select>
      </Styled.TopSection>
      {stockCandles.s === "ok" ? (
        <StockCandlesDiagram stockCandles={stockCandles} />
      ) : (
        <p className="">No data</p>
      )}
    </Styled.StockCandlesContainer>
  );
};
