import React, { useState } from "react";
import * as Styled from "./styles";
import axios from "axios";

export const Dashboard = () => {
  const [searchValue, setSearchValue] = useState({
    name: "",
    dateFrom: "",
    dateTo: "",
  });
  const [companyProfileValues, setCompanyProfileValues] = useState({});

  const onFormSubmit = (e) => {
    e.preventDefault();
    console.log("Form submit working", searchValue);

    getCompanyProfile();
  };

  const getCompanyProfile = () => {
    const TOKEN = `cbogg62ad3i94d2l9a30`;
    const API = `https://finnhub.io/api/v1/stock/profile2?symbol=${searchValue.name}&token=${TOKEN}`;

    try {
      axios.get(API).then((response) => {
        setCompanyProfileValues(response.data);
      });
    } catch (err) {
      console.log(err);
    }
  };

  const handleOnChange = (event) => {
    const { name, value, type } = event.target;

    console.log("TYPE", type);

    setSearchValue((prev) => {
      if (type === "date") {
      }
      return { ...prev, [name]: value };
    });
  };
  console.log(companyProfileValues);
  return (
    <Styled.Container>
      <form onSubmit={onFormSubmit}>
        <input
          placeholder="Company name"
          name="name"
          onChange={handleOnChange}
        />
        <input type="date" name="dateFrom" onChange={handleOnChange} />
        <input type="date" name="dateTo" onChange={handleOnChange} />
        <button>Submit</button>
      </form>

      <div>
        {Object.keys(companyProfileValues).length > 0 && (
          <div>
            <div>name : {companyProfileValues.name}</div>
            <div>country : {companyProfileValues.country}</div>
            <div>currency : {companyProfileValues.currency}</div>
            <div>web url : {companyProfileValues.weburl}</div>
          </div>
        )}
      </div>
    </Styled.Container>
  );
};
