import React, { useEffect, useState } from "react";
import * as Styled from "./styles";
import axios from "axios";

/**
 *
 * @returns
 */
export const Dashboard = () => {
  const [searchValue, setSearchValue] = useState({
    name: "",
    dateFrom: "",
    dateTo: "",
  });
  const [symbols, setSymbols] = useState([]);
  const [count, setCount] = useState(0);
  const [companyProfileValues, setCompanyProfileValues] = useState([]);
  const [stockCandles, setStockCandles] = useState({});

  const onFormSubmit = async (e) => {
    e.preventDefault();
    setSymbols([]);
    setCount(0);
    setCompanyProfileValues([]);
    try {
      const responseSymbols = await getSymbols();
      setSymbols(responseSymbols.result);
      setCount(responseSymbols.count);

      getCompanyProfiles(responseSymbols.result);
      console.log("SYMBOLS ", responseSymbols);
    } catch (error) {
      console.log(error);
    }

    // getStockCandles();
  };

  const getCompanyProfiles = async (sym, from = 0, to = 5) => {
    try {
      let companyProfiles = await Promise.all(
        sym
          .slice(from, sym.length > to ? to : sym.length)
          .map(async (element) => await getSingleCompanyProfile(element.symbol))
      );
      companyProfiles = companyProfiles.filter((element) => {
        element?.name ? true : false;
      });

      setCompanyProfileValues(companyProfileValues.concat(companyProfiles));
    } catch (error) {
      console.log(error);
    }
  };
  const getSingleCompanyProfile = async (companySymbol) => {
    return new Promise((resolve, reject) => {
      console.log("single symbol to be fetched", companySymbol);
      const TOKEN = `cbogg62ad3i94d2l9a30`;
      const FINNHUB_COMPANY_PROFILE_API = `https://finnhub.io/api/v1/stock/profile2?symbol=${companySymbol}&token=${TOKEN}`;
      axios
        .get(FINNHUB_COMPANY_PROFILE_API)
        .then((response) => {
          console.log(response);
          resolve(response.data);
        })
        .catch((err) => {
          console.log(err);
          return reject(err);
        });
    });
  };

  const getSymbols = async () => {
    return new Promise((resolve, reject) => {
      const TOKEN = `cbogg62ad3i94d2l9a30`;
      const FINNHUB_COMPANY_SYMBOLS_API = `https://finnhub.io/api/v1/search?q=${searchValue.name}&token=${TOKEN}`;

      axios
        .get(FINNHUB_COMPANY_SYMBOLS_API)
        .then((response) => resolve(response.data))
        .catch((err) => reject(err));
    });
  };

  const getStockCandles = () => {
    const TOKEN = `cbogg62ad3i94d2l9a30`;
    const FINNGUB_STOCK_CANDLES_API = `https://finnhub.io/api/v1/stock/candle?symbol=${searchValue.name}&resolution=1&from=${searchValue.dateFrom}&to=${searchValue.dateTo}&token=${TOKEN}`;
    try {
      axios.get(FINNGUB_STOCK_CANDLES_API).then((response) => {
        console.log("STOCK CANDLES", response.data);
        setStockCandles(response.data);
      });
    } catch (err) {
      console.log(err);
    }
  };

  const convertToUnix = (date) => {
    return Math.floor(new Date(date).getTime() / 1000);
  };

  const handleOnChange = (event) => {
    const { name, value, type } = event.target;

    setSearchValue((prev) => {
      if (type === "date") {
        let convertedDate = convertToUnix(value);
        return { ...prev, [name]: convertedDate };
      } else return { ...prev, [name]: value };
    });
  };

  return (
    <Styled.Container>
      <form onSubmit={onFormSubmit}>
        <input
          placeholder="Company name"
          name="name"
          onChange={handleOnChange}
        />
        <input type="date" name="dateFrom" onChange={handleOnChange} />
        <input type="date" name="dateTo" onChange={handleOnChange} />
        <button>Submit</button>
      </form>

      <div>
        {companyProfileValues?.map((companyProfile, index) => (
          <div key={index} className="single">
            <div>name : {companyProfile.name}</div>
            <div>country : {companyProfile.country}</div>
            <div>currency : {companyProfile.currency}</div>
            <div>web url : {companyProfile.weburl}</div>
          </div>
        ))}
      </div>

      {companyProfileValues.length < count && (
        <button
          onClick={() =>
            getCompanyProfiles(
              symbols,
              companyProfileValues.length + 1,
              companyProfileValues.length + 5
            )
          }
        >
          Get more
        </button>
      )}
      <div>
        {Object.keys(stockCandles).length > 0 && (
          <div>
            {/* {stockCandles.map((item, index) => (
              <div>
                <div>name : {companyProfileValues.name}</div>
                <div>country : {companyProfileValues.country}</div>
                <div>currency : {companyProfileValues.currency}</div>
                <div>web url : {companyProfileValues.weburl}</div>
              </div>
            ))} */}
          </div>
        )}
      </div>
    </Styled.Container>
  );
};
