  const [searchValue, setSearchValue] = useState("");

  const onFormSubmit = (e) => {
    e.preventDefault();
    console.log("Form submit working", searchValue);
  };

  const getCompanyProfile = () => {
    const TOKEN = `cbogg62ad3i94d2l9a30`;
    const API = `https://finnhub.io/api/v1/stock/profile2?symbol=AAPL&token=${TOKEN}`;

    try {
      axios.get(GEOCODING_API).then((response) => {
        if (response.data.results.length !== 0) {
          setCustomerLocation(response.data.results[0].geometry.location);
        } else {
          setCustomerLocation({
            lat: "Could not find",
            lng: "Could not find",
          });
        }
      });
    } catch (err) {
      console.log(err);
    }
  };

  const handleOnChange = (event) => {
    setSearchValue(event.target.value);
  };

  return (
    <Styled.Container>
      <form onSubmit={onFormSubmit}>
        <input placeholder="Company name" onChange={handleOnChange} />
        <button>Submit</button>
      </form>
    </Styled.Container>
  );
};
